    AND A.LEVEL_7 BETWEEN NVL(P_LEVEL_7, A.LEVEL_7)
    AND DECODE(
            P_7_LEVEL_7,
            NULL, DECODE(P_LEVEL_7, NULL, A.LEVEL_7, P_LEVEL_7),
