        )
    AND A.LEVEL_8 BETWEEN NVL(P_LEVEL_8, A.LEVEL_8)
    AND DECODE(
            P_8_LEVEL_8,
            NULL, DECODE(P_LEVEL_8, NULL, A.LEVEL_8, P_LEVEL_8),
            P_8_LEVEL_8
        )
    AND D.MONTH(+) = NVL(TO_NUMBER(TO_CHAR(P_DATE, 'MM')), D.MONTH(+))
    AND DECODE(
