    AND B.LEVEL_1(+) = A.LEVEL_1
    AND B.LEVEL_2(+) = A.LEVEL_2
    AND B.LEVEL_3(+) = A.LEVEL_3
    AND B.LEVEL_4(+) = A.LEVEL_4
    AND B.LEVEL_5(+) = A.LEVEL_5
    AND B.LEVEL_6(+) = A.LEVEL_6
    AND B.LEVEL_7(+) = A.LEVEL_7
    AND B.LEVEL_8(+) = A.LEVEL_8
    AND D.COMPANY_CODE(+) = A.COMPANY_CODE
    AND D.LEVEL_1(+) = A.LEVEL_1
    AND D.LEVEL_2(+) = A.LEVEL_2
    AND D.LEVEL_3(+) = A.LEVEL_3
    AND D.LEVEL_4(+) = A.LEVEL_4
    AND D.LEVEL_5(+) = A.LEVEL_5
    AND D.LEVEL_6(+) = A.LEVEL_6
    AND D.LEVEL_7(+) = A.LEVEL_7
    AND D.LEVEL_8(+) = A.LEVEL_8
    AND A.LEVEL_1 = C.LEVEL_1
    AND A.LEVEL_2 = C.LEVEL_2
    AND A.LEVEL_3 = C.LEVEL_3
    AND A.LEVEL_4 = C.LEVEL_4
    AND A.LEVEL_5 = C.LEVEL_5
    AND A.LEVEL_6 = C.LEVEL_6
    AND C.LEVEL_1 BETWEEN 400 AND 599
    AND B.YEAR(+) = TO_NUMBER(TO_CHAR(P_REPORT_DATE, 'YYYY'))
    AND D.YEAR(+) = TO_NUMBER(TO_CHAR(P_REPORT_DATE, 'YYYY'))
    AND A.TXN_ENTRY_RECEIVED = 'S'
    AND C.ACCOUNT_CLASS NOT IN ('ENC', 'SUM')
ORDER BY
    A.ACCOUNT



