CREATE OR REPLACE FUNCTION GM_F_DESPLEGAR_R (
    PV_NIVEL1           IN VARCHAR2,
    PV_NIVEL2           IN VARCHAR2,
    PV_NIVEL3           IN VARCHAR2,
    PV_NIVEL4           IN VARCHAR2,
