    GM_P_DISPLAY_R(
        PV_LEVEL_1       => PV_NIVEL1,
        PV_2_LEVEL       => PV_NIVEL2,
        PV_LEVEL_3       => PV_NIVEL3,
        PV_LEVEL_4       => PV_NIVEL4,
