CREATE OR REPLACE FORCE EDITIONABLE VIEW "GM_V_COMPARATIVES" ("COMPANY", "APPLICATION_CODE", "BREAK1", "TITLE1", "EARLY_R_TERMINATION_DATA_1_CODE", "BREAK_DATA1", "BREAK2", "TITLE2", "EARLY_TERMINATION_DATA_2_CODE", "DATA_ROMPIMIENTO2", "BREAK3", "TITLE3", "EARLY_R_TERMINATION_DATA_3_CODE", "DATA_ROMPIMIENTO3", "BREAK4", "TITLE4", "EARLY_R_TERMINATION_DATA_4_CODE", "DATA_ROMPIMIENTO4", "BREAK5", "TITLE5", "EARLY_R_TERMINATION_DATA_5_CODE", "DATA_ROMPIMIENTO5", "BREAK6", "TITLE6", "EARLY_R_TERMINATION_DATA_6_CODE", "DATA_ROMPIMIENTO6", "BREAK7", "TITLE7", "EARLY_R_TERMINATION_DATA_7_CODE", "DATA_ROMPIMIENTO7", "LEVEL1", "LEVEL2", "LEVEL3", "LEVEL4", "LEVEL5", "LEVEL6", "LEVEL7", "LEVEL8", "BRANCH_CODE", "SUBAPPLICATION_CODE", "TXN_ENTRY_AMOUNT", "CURRENCY_CODE", "LINE_FINANCIERA_CODE", "INVESTMENT_CODE", "BANK_TYPE", "PRODUCT_CODE", "TXN_ENTRY_DATE") AS 
  SELECT   b.COMPANY_CODE COMPANY,
              a.application_code,
              a.FIELD_NAME BREAK1,
              Gm_Comparative_Field_Title (
                 a.application_code,
                 Gm_Field_Comparative (a.application_code, 1)
              )
                 TITLE1,
              CASE
                 WHEN Gm_Field_Comparative (a.application_code, 1) LIKE
                         'CODE%CURRENCY%'
                 THEN
                    TO_CHAR (b.currency_code)
                 WHEN Gm_Field_Comparative (a.application_code, 1) LIKE
                         'CODE%BRANCH%'
                 THEN
                    TO_CHAR (b.branch_code)
                 WHEN Gm_Field_Comparative (a.application_code, 1) LIKE
                         'CODE%SUBSTITUTE%APPLICATION%'
                 THEN
