                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%TIP%LOAN%'
                 THEN
                    TO_CHAR (b.loan_type_code)
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'TYP%BANK%'
                 THEN
                    TO_CHAR (b.BANK_TYPE)
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%PRODUCT%'
                 THEN
                    TO_CHAR (b.product_code)
              END
                 EARLY_R_TERMINATION_DATA_7_CODE,
              CASE
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%CURRENCY%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.currency_code
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%BRANCH%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.branch_code
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%SUBSTITUTE%APPLICATION%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.subapplication_code
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%FINANCING%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.LINE_FINANCIERA_CODE
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%INVOICES%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.investment_code
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%TIP%LOAN%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.loan_type_code
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'TYP%BANK%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.BANK_TYPE
                    )
                 WHEN Gm_Field_Comparative (a.application_code, 7) LIKE
                         'CODE%PRODUCT%'
                 THEN
                    Gm_F_Comparative_Field_Data (
                       1,
                       a.application_code,
                       Gm_Field_Comparative (a.application_code, 7),
                       b.product_code
                    )
              END
                 DATA_ROMPIMIENTO7,
              b.level_1 LEVEL1,
              b.level_2 LEVEL2,
              b.level_3 LEVEL3,
              b.level_4 LEVEL4,
              b.level_5 LEVEL5,
              b.level_6 LEVEL6,
              b.level_7 LEVEL7,
              b.level_8 LEVEL8,
              b.branch_code,
              b.subapplication_code,
              b.TXN_ENTRY_AMOUNT,
              b.currency_code,
              b.LINE_FINANCIERA_CODE,
              b.investment_code,
              b.BANK_TYPE,
              b.product_code,
              b.TXN_ENTRY_DATE
       FROM   GM_PARAMETER_COMPARATIVE a, GM_AUXILIARY_COMPARATIVE b
      WHERE   a.application_code = b.application_code
              AND a.ORDER_PRIORITY = 1
   ORDER BY   a.application_code
;

