        GN_AMOUNTORIGIN               => GN_MONTOORIGEN,
        GN_AMOUNTDESTINATION          => GN_MONTODESTINO,
        GN_DIFFERENCEEXCHANGE         => GN_DIFERENCIACAMBIO,
        GN_LOSSGAIN                   => GN_GANANCIAPERDIDA,
        GV_MESSAGEERROR               => GV_MENSAJEERROR
    );

    RETURN GN_MONTODESTINO;

EXCEPTION
    WHEN OTHERS THEN
